import React from 'react';

export default function ResizeHandle(props) {
    const onMouseDown = (event) => {
        event.stopPropagation(); // Prevent bbox drag functionality
        const startX = event.clientX;
        const startY = event.clientY;
        const startWidth = parseInt(
            window.getComputedStyle(props.box).width,
            10
        );
        const startHeight = parseInt(
            window.getComputedStyle(props.box).height,
            10
        );

        const onMouseMove = (event) => {
            const newWidth = startWidth + event.clientX - startX;
            const newHeight = startHeight + event.clientY - startY;
            props.box.style.width = `${newWidth}px`;
            props.box.style.height = `${newHeight}px`;
        };

        const onMouseUp = () => {
            document.removeEventListener('mousemove', onMouseMove);
            document.removeEventListener('mouseup', onMouseUp);
        };

        document.addEventListener('mousemove', onMouseMove);
        document.addEventListener('mouseup', onMouseUp);
    };

  return (
    <div className="resize-handle" onMouseDown={onMouseDown}>
      {props.children}
    </div>
  );
}
