export default function ResizeHandle({ onResize }) {
  const onMouseDown = (event) => {
      event.stopPropagation(); // Prevent bbox drag functionality
      const startX = event.clientX;
      const startY = event.clientY;

      const onMouseMove = (event) => {
          const widthChange = event.clientX - startX;
          const heightChange = event.clientY - startY;
          onResize(widthChange, heightChange);
      };

      const onMouseUp = () => {
          document.removeEventListener('mousemove', onMouseMove);
          document.removeEventListener('mouseup', onMouseUp);
      };

      document.addEventListener('mousemove', onMouseMove);
      document.addEventListener('mouseup', onMouseUp);
  };

  return (
      <div className="resize-handle" onMouseDown={onMouseDown}></div>
  );
}
